def repr_kwargs_dict(x, *, align_equals=True):
    """
    EXAMPLE:
        >>> x = {
        ...     "instance_data_root": "/root/CleanCode/Github/CogVideo/finetune/datasets/Disney-VideoGeneration-Dataset",
        ...     "dataset_name": None,
        ...     "dataset_config_name": None,
        ...     "caption_column": "prompts.txt",
        ...     "video_column": "videos.txt",
        ...     "height": 480,
        ...     "width": 720,
        ...     "fps": 8,
        ...     "max_num_frames": 49,
        ...     "skip_frames_start": 0,
        ...     "skip_frames_end": 0,
        ...     "cache_dir": "~/.cache",
        ...     "id_token": None,
        ... }
        ... 
        ... print(dict_repr(x),align_equals=True)
        ... 
        ... #RESULTS:
        ... #    dict(
        ... #         instance_data_root  = '/root/CleanCode/Github/CogVideo/finetune/datasets/Disney-VideoGeneration-Dataset',
        ... #         dataset_name        = None,
        ... #         dataset_config_name = None,
        ... #         caption_column      = 'prompts.txt',
        ... #         video_column        = 'videos.txt',
        ... #         height              = 480,
        ... #         width               = 720,
        ... #         fps                 = 8,
        ... #         max_num_frames      = 49,
        ... #         skip_frames_start   = 0,
        ... #         skip_frames_end     = 0,
        ... #         cache_dir           = '~/.cache',
        ... #         id_token            = None,
        ... #    )
    """
    lines = ["dict("]
    keys = list(x.keys())
    values = list(x.values())

    assert all(isinstance(key, str) for key in keys)

    max_key_length = max(map(len, keys))

    for key, value in x.items():
        assert isinstance(key, str)
        indent = "     "
        equals = " = "
        if align_equals:
            equals = equals.rjust(max_key_length - len(key) + len(equals))
        new_line = indent + key + equals + repr(value) + ","
        lines.append(new_line)
    lines.append(")")

    return line_join(lines)

