def mean(*x):
    """
    A super basic mean calculator
    Works with any datatype that supports + and /

    EXAMPLES:
     >>> mean(1,2,3)
     >>> mean(1,2,3)
    ans = 2.0
     >>> mean(123)
    ans = 123.0
     >>> mean([1,5,2,4])
    ans = 3.0
    """
    x=detuple(x)
    return sum(x)/len(x)

